let fusion_textarea_tab=function(a){$(document).delegate("#"+a,"keydown",function(b){let keyCode=b.keyCode||b.which;if(keyCode==9){b.preventDefault();let start=$(this).get(0).selectionStart;let end=$(this).get(0).selectionEnd;$(this).val($(this).val().substring(0,start)+"\t"+$(this).val().substring(end));$(this).get(0).selectionStart=$(this).get(0).selectionEnd=start+1}})};